function convertToRoman(num) {

    let romanNum = "";

    let charLetters = ['M', 'D', 'C', 'L', 'X', 'V', 'I'];
    let charValues = [1000, 500, 100, 50, 10, 5, 1];
    let i = 0;

    while (i < 5) {

        var toAdd = 0;

        toAdd = Math.floor(num/charValues[i]);
        num -= (charValues[i] * toAdd);
        for (var j = 0; j < toAdd; j++) {
            romanNum += charLetters[i];
        }

        if (num >= charValues[i] - charValues[i+2]) { 
            num -= (charValues[i] - charValues[i+2]);
            romanNum += charLetters[i+2] + charLetters[i];
        } else if 

        (num >= charValues[i+1]) {
            num -= (charValues[i+1]);
            romanNum += charLetters[i+1];
        } else if

        (num >= charValues[i+1] - charValues[i+2]) {
            num -= (charValues[i+1] - charValues[i+2])
            romanNum += charLetters[i+2] + charLetters[i+1];
        }

        i += 2;
    }

    if (num%5 != 4) {
        for (var k = 0; k < num; k++) {
            romanNum += charLetters[6];
        }
    }

    return romanNum;
}

